<script setup>
import {useGlobalStore} from '@/store/globalStore'
import _ from "lodash";
import {computed} from "vue";
const globalStore = useGlobalStore();

const socialLinks = computed(() => {
    if(_.isEmpty(globalStore.getSocialLinks)) return null
    let social_links = []
    Object.keys(globalStore.getSocialLinks).forEach(key => {
        social_links.push({
            type: key,
            link: globalStore.getSocialLinks[key]
        })
    })

    return social_links
})
</script>

